function getPublicUrl(filePath, provider) {
  const relativePath = filePath.replace(/\\/g, ''/'');

  if (provider === ''qiniu'') {
    if (process.env.MEDIA_BASE_URL) {
      return \`${process.env.MEDIA_BASE_URL}/${relativePath}\`;
    }
    if (process.env.QINIU_DOMAIN) {
      return \`https://${process.env.QINIU_DOMAIN}/${relativePath}\`;
    }
  } else {
    const baseUrl = process.env.BASE_URL || (process.env.NODE_ENV === ''production'' ? ''https://asheblog.org'' : ''http://localhost:4000'');
    const cleanRelativePath = relativePath.startsWith(''/'') ? relativePath.substring(1) : relativePath;
    const finalUrl = \`${baseUrl}/${cleanRelativePath}\`;
    return finalUrl;
  }

  const baseUrl = process.env.BASE_URL || (process.env.NODE_ENV === ''production'' ? ''https://asheblog.org'' : ''http://localhost:4000'');
  const cleanRelativePath = relativePath.startsWith(''/'') ? relativePath.substring(1) : relativePath;
  const finalUrl = \`${baseUrl}/${cleanRelativePath}\`;
  return finalUrl;
}

